            },
            yaxis: {
              axisBorder: {
                show: false
              },
              axisTicks: {
                show: false,
              },
              labels: {
                show: false,
                formatter: function (val) {
                  return val + "%";
                }
              }
            },
            title: {
              text: 'Monthly Inflation in Argentina, 2002',
              floating: true,
              offsetY: 320,
              align: 'center',
              style: {
                color: '#444'
              }
            }
          },





 options1: {
            plotOptions: {
              bar: {
                horizontal: true,
                dataLabels: {
                  position: 'top',
                },
              }
            },

            dataLabels: {
              enabled: false,
              offsetX: -6,
              style: {
                fontSize: '12px',
                colors: ['#1df20a']
              }
            },
            stroke: {
              show: true,
              width: 1,
              colors: ['#fff']
            },

            xaxis: {
              categories:["Net Profit/(Loss) …"],
            }
          },


options2: {
        plotOptions: {
              bar: {
                horizontal: true,
                dataLabels: {
                  position: 'top',
                },
              }
            },




         	title: {
				text: "BUDGET ACHIEVEMENT"
			},
			subtitles: [{
				centertext: "100.00% Positive",
				verticalAlign: "center",
				fontSize: 50,

			}],
			data: [{
				type: "doughnut",
				color: "#1df20a",
				showInLegend: true,


			}]
          },
          series2: [],

          series: [{
            name: 'SQBL',
            data: [4.0, 6.0]
          },
{
            name: 'SQCL',
            data: [2.3, 3.0]
          },

          ],

           series1: [{
            name: 'SQBL',
            data: [44, 64]
          }, {
            name: 'SQCL',
            data: [53, 75]
          }],

        }
      }


  render() {
    return (

    <div>

    <h1>FINANCIAL YEAR</h1>
    <ul className="card-statistics-block">
			<button  className="btn btn-outline-secondary "><span>2019</span></button>
			</ul>
			<ul className="card-statistics-block">


	  <a class="btn btn-outline-secondary" href="#">Jul</a>

      <a class="btn btn-outline-secondary" href="#">Aug</a>
      <a class="btn btn-outline-secondary" href="#">Set</a>
      <a class="btn btn-outline-secondary" href="#">Oct</a>
      <a class="btn btn-outline-secondary" href="#">Nov</a>
      <a class="btn btn-outline-secondary" href="#">Dec</a>
      <a class="btn btn-outline-secondary" href="#">Jan</a>
      <a class="btn btn-outline-secondary" href="#">Feb</a>
      <a class="btn btn-outline-secondary" href="#">Mar</a>
      <a class="btn btn-outline-secondary" href="#">Apr</a>
      <a class="btn btn-outline-secondary" href="#">May</a>
      <a class="btn btn-outline-secondary" href="#">Jun</a>

</ul>

    <Col xs={24} sm={24} md={12} lg={24} className="c-mb">

                <Card
                  bordered={false}
                  className="mi-card mi-card-boxshadow card-statistics"
                  style={{ minHeight: "100%" }}
                >
                  <div className="card-statistics-block">

                  <ReactApexChart options={this.state.options1} series={this.state.series1} type="bar" width={300} height={150} />
                  <ReactApexChart options={this.state.options} series={this.state.series} type="bar" width={300} height={150} />

                  <ReactApexChart options={this.state.options2} series={this.state.series2} type="" width={300} height={150} />
                   <h1 style={{color: "red", marginRight: '5px'}}> 100.00%</h1>
                     </div>

              <div className="card-statistics-block">

                  <ReactApexChart options={this.state.options1} series={this.state.series1} type="bar" width={300} height={150} />
                  <ReactApexChart options={this.state.options} series={this.state.series} type="bar" width={300} height={150} />
                   <h1 style={{color: "red"}}> 100.00%</h1>
                  <ReactApexChart options={this.state.options2} series={this.state.series2} type="" width={300} height={150} />
                     </div>

                     <div className="card-statistics-block">

                  <ReactApexChart options={this.state.options1} series={this.state.series1} type="bar" width={300} height={150} />
                  <ReactApexChart options={this.state.options} series={this.state.series} type="bar" width={300} height={150} />
                   <h1 style={{color: "red"}}> 100.00%</h1>
                  <ReactApexChart options={this.state.options2} series={this.state.series2} type="" width={300} height={150} />
                     </div>

                </Card>





            </Col>

</div>


    )
  }
}

